CREATE TABLE "videos" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "deleted_at" timestamptz NULL, "updated_at" timestamptz NOT NULL, "name" character varying NULL, "url" character varying NULL, "size" bigint NOT NULL DEFAULT 0, "duration" character varying NULL, "uploaded_at" timestamptz NULL, "created_by" bigint NOT NULL, "updated_by" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "videos_admins_video_creator" FOREIGN KEY ("created_by") REFERENCES "admins" ("id") ON DELETE NO ACTION, CONSTRAINT "videos_admins_video_updater" FOREIGN KEY ("updated_by") REFERENCES "admins" ("id") ON DELETE NO ACTION);
CREATE TABLE "ipc_events" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "deleted_at" timestamptz NULL, "updated_at" timestamptz NOT NULL, "device_brand" bigint NOT NULL DEFAULT 0, "device_model" bigint NOT NULL DEFAULT 0, "device_id" character varying NOT NULL, "event_id" character varying NOT NULL, "event_time" timestamptz NOT NULL, "event_type" bigint NOT NULL DEFAULT 0, "event_status" bigint NOT NULL DEFAULT 1, "images" jsonb NULL, "labeled_images" jsonb NULL, "description" character varying NULL, "raw_data" character varying NULL, "created_by" bigint NOT NULL, "updated_by" bigint NOT NULL, "video_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "ipc_events_admins_ipc_event_creator" FOREIGN KEY ("created_by") REFERENCES "admins" ("id") ON DELETE NO ACTION, CONSTRAINT "ipc_events_admins_ipc_event_updater" FOREIGN KEY ("updated_by") REFERENCES "admins" ("id") ON DELETE NO ACTION, CONSTRAINT "ipc_events_videos_ipc_event_video" FOREIGN KEY ("video_id") REFERENCES "videos" ("id") ON DELETE SET NULL);
