CREATE TABLE "users" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "create_time" timestamptz NOT NULL, "delete_time" timestamptz NULL, "update_time" timestamptz NOT NULL, "username" character varying NOT NULL, "password" character varying NOT NULL, "nickname" character varying NOT NULL, "real_name" character varying NOT NULL, "mobile" character varying NULL, "wechat_openid" character varying NULL, "avatar" jsonb NULL, "user_status" bigint NOT NULL DEFAULT 1, "updater_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "users_admins_user_updater" FOREIGN KEY ("updater_id") REFERENCES "admins" ("id") ON DELETE NO ACTION);
